INSERT INTO items (theme, name, caption, rank, trivia, filename) VALUES('pets', 'billy', 'Billy', 2, "<p1>Hi, my name is Billy. Look, I know what you are thinking. Rescue dog. More collateral damage from Mike Baird's anti-greyhound racing policies. But racing does not define me. I am so much more. Hang on. Sniff. Rabbit? </p> <p style='color:silver;font-size:80%;font-style:italic'>Like me and support me through my re-invention process.</p>", '/data/images/pets/billy.jpg');
INSERT INTO items (theme, name, caption, rank, trivia, filename) VALUES('pets', 'deedee', 'DeeDee', 3, "<p1>Hi, my name is DeeDee. My master, and I use that term deliberately, trained me to pick up pizza using a little wooden trailer. Like hello, the humiliation of it all. And not even a measly tip. Tipsy? My paw.</p> <p style='color:silver;font-size:80%;font-style:italic'>Like me so I can at least earn a minimum wage.</p>", '/data/images/pets/deedee.jpg');
INSERT INTO items (theme, name, caption, rank, trivia, filename) VALUES('pets', 'neo', 'Neo', 4, "<p1>Hi, my name is Neo. So Dad has got all these great photos and pick this one. He reckons I fight too much. But has he ever stopped to think why? Maybe if he spent more time with me I might be able to work through some issues.</p> <p style='color:silver;font-size:80%;font-style:italic'>Like me so I can avoid a life in therapy.</p>", '/data/images/pets/neo.jpg');
